import React, { useState, useEffect } from 'react'
import axios from 'axios'
import personService from './services/persons'

const App = () => {
  const [persons, setPersons] = useState([
    {
      name: 'Arto Hellas',
      number: '040-1231244'
    }
  ])

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [errorMessage, setErrorMessage] = useState('some error happened...')

  useEffect(() => {
    personService
      .getAll()
      .then(initialPersons => {
        setPersons(initialPersons)
      })}, [])

  console.log('render', persons.length, 'persons')

  const addPerson = (event) => {

    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }

    const duplicate = persons.find(person => personObject.name === person.name)

    if (persons.includes(duplicate)) {
      // window.alert(`${newName} is already added to phonebook`)
      let duplicateID = duplicate.id
      if (window.confirm(`${duplicate.name} is already added to the phonebook, replace the old number with a new one?`)) {
        axios
          .put(`http://localhost:3001/persons/${duplicateID}`, personObject)
          window.location.reload()
      }
    } else {
      personService
        .create(personObject)
        .then(returnedPerson => {
          setPersons(persons.concat(returnedPerson))
        })
    }
  }

  const Notification = ({ message }) => {
    if(message === null) {
      return null
    }

    return (
      <div className="error">
        {message}
      </div>
    )
  }

  const removePerson = (name) => {

    if(window.confirm(`Delete ${name}?`)) {
      const newPersons = persons.filter((item) => item.name !== name)
      let id = persons.find((item) => item.name === name).id
      setPersons(newPersons)

      personService.remove(id)
    }
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      {/* <Notification message={errorMessage}/> */}
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName}
            onChange={handlePersonChange}
          />
        </div>
        <div>number: <input value={newNumber}
          onChange={handleNumberChange}
        />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <a>
        {persons.map(person =>
          <a key={person.name}>
            {person.name}, {" "}
            {person.number}, {" "}
            <button type="button" onClick ={ () => removePerson(person.name)}>
              remove
            </button>
            <br/>
          </a>
        )}
      </a>
    </div>
  )

}

export default App
