import React, { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ])
  const [newName, setNewName] = useState('')

  const taulu = [1, 2, 3]

  const addPerson = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName
    }

    console.log("personObject: ", personObject)
    console.log("taulu: ", taulu)
    console.log("persons: ", persons)
    console.log(persons.map(person => person.name))
    const search = personObject.name
    console.log(search)
    console.log(persons.includes(serach))
    if (persons.map(person => person.name === personObject.name) === true) {
      setPersons(persons.concat(personObject))
      setNewName('')
    } else {
      window.alert("löytyy jo")
    }
  }

  const handlePersonChange = (event) => {
    setNewName(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addPerson}>
        <div>
          name: <input value={newName}
            onChange={handlePersonChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.map(person =>
          <li key={person.name}>
            {person.name}
          </li>
        )}
      </ul>
    </div>
  )

}

export default App
